ingredientsDB = msg.payload.ingredientsDB;
ingredients = msg.payload.ingredients


n_ingredients = ingredients.length;
n_ingredients_db = ingredientsDB.length;
var cost_recipe = 0;

var i; 
var j;

var ingredient;
var amount;


for (j=0; j < n_ingredients;j++){

    ingredient = ingredients[j].Product;
    amount = ingredients[j].kg;


    for (i=0; i < n_ingredients_db;i++){

        if(ingredient == ingredientsDB[i].Product){
            cost_recipe += ingredientsDB[i].CO2 * amount;
            // cost_ingredients[j] = ingredientsDB[i].CO2 * amount;
            ingredients[j].CO2 = ingredientsDB[i].CO2 * amount
            // ingredients[j].CO3 = amount

            break;
        }
    }
}

msg.payload.cost_recipe = cost_recipe
msg.payload.color = cost_recipe/30

delete msg.payload.ingredientsDB


return msg;

